import React, { Component } from 'react';

class ProductForm extends Component {
    state = {input: '' }

    componentDidMount = async() => {

        // if (!this.props.contract || !this.props.accounts) {
        //     return <h2> PLEASE WAIT... </h2>;
        // }

    };

    onSubmit = (e) => {
        e.preventDefault();
        // this.props.contract.methods.addProduct('1', this.state.input).send({from: this.props.accounts[0]});
        // this.logProduct();
        console.log(this.state.input)
        this.setState({ input: '' });
      }

    onChange = (e) => this.setState({ input : e.target.value });

    // logProduct = async () => {
    //     const product = await this.props.contract.methods.getProductDetails('5').call();
    //     console.table(product);
    // }


    render() {

        return (
            <form onSubmit= {this.onSubmit} className='form-container' method='post'>
                <div className="form-row">
                <h4> Choose Material </h4>
                {/* <input onChange= { this.onChange } type="text" id="product-name" 
                placeholder="Enter Material Name" autoComplete="off"/> */}
                <select onChange = {this.onChange}>
                    <option id='1' value='vitamin-a'>VITAMIN A </option>
                    <option id='2' value='vitmain-b'>VITAMIN B COMPLEX</option>
                    <option id='3' value='vitamin-c'>VITAMIN C EXTRACT</option>
                    <option id='4' value='vitamin-d'>VITAMIN D</option>
                    <option id='5' value='potassium'>POTASSIUM</option>
                    <option id='6' value='zinc'>ZINC</option>
                </select>
                <input style= {{ marginLeft: '10px', cursor:'pointer'}} className="btn" type="submit" value="SEARCH"/>
                </div>


            </form>

         );

    }
}

export default ProductForm;
