import React, { Component } from 'react';

class ProductForm extends Component {

    state = {input: '' }

    componentDidMount = async() => {
        if (!this.props.contract || !this.props.accounts) {
            return <div> PLEASE WAIT... </div>;
        }
        await this.props.contract.methods.addProduct('1', this.state.input).send({from: this.props.accounts[0]});

    };

    onSubmit = (e) => {
        e.preventDefault();

        this.setState({ input: '' });
      }
    onChange = (e) => this.setState({ input : e.target.value });


    render() {

        return (

            <form onSubmit= {this.onSubmit} className='form-container'>
                <h2> Create a Product! </h2>
                <div className="form-row">
                <input onChange= { this.onChange } type="text" id="product-name" placeholder="Enter Product Name"/>
                </div>
                <input style= {{cursor:'pointer'}} className="btn" type="submit" value="CREATE"/>

            </form>
         );

    }
}

export default ProductForm;
