    let contract = this.props.contract;
    if (!account || !contract) {
      return <div>Loading ..... </div>;
    }
    return (
      <BrowserRouter>
        <div className="product-form-container">
          <div className="side-nav">
            <ul className="mini-nav-list">
              <li className="link-item">
                <NavLink to="/add-product/createProduct">
                  + CREATE PRODUCT
                </NavLink>{" "}
              </li>
              <li className="link-item">
                {" "}
                <NavLink to="/add-product/addMaterial">+ ADD PRODUCT SPECS</NavLink>
              </li>
              <li className="link-item">
                {" "}
                <NavLink to="/add-product/costPlan">+ CREATE COST PLAN</NavLink>
              </li>
            </ul>
          </div>
          <div className="main-content">
            <Route
              path="/add-product/createProduct"
              exact
              render={(props) => (
                <CreateProduct
                  {...props}
                  account={this.props.accounts}
                  contract={this.props.contract}
                />
              )}
            />
            <Route
              path="/add-product/addMaterial"
              exact
              render={(props) => (
                <AddMaterial
                  {...props}
                  account={this.props.accounts}
                  contract={this.props.contract}
                />
              )}
            />
            <Route
              path="/add-product/costPlan"
              exact
              render={(props) => (
                <CreateCostPlan
                  {...props}
                  account={this.props.accounts}
                  contract={this.props.contract}
                />
              )}
            />
          </div>
        </div>
      </BrowserRouter>
    );
  }
}

