import React, {Component} from 'react' 

class Track extends Component {


    componentDidMount = async() => {

        console.log(this.props.account,this.props.contract)


    }

    state = {requestID : ''}
    constructor(props) {
        super(props);
        this.requestIdRef = React.createRef();
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleChange.bind(this);
    }

    handleSubmit = async(e) => {
            e.preventDefault();
            let id = parseInt(this.state.requestID,10);
            // await this.props.contract.methods.createLog(1, 'SHIPPED' ,'20C', '25%', 'NORMAL').send({from: this.props.account})
            // const response = await this.props.contract.methods.getTrackLogs(id).call()
            this.setState({
                requestID: ''
            })
            console.log(id);
    }

    handleChange = async (e) => {
        this.setState({
            requestID: this.requestIdRef.current.value
        })
    }

    render() {
        let acc = this.props.account;
        let cont = this.props.contract;

        if (!acc && !cont) {
            return <div> loading..... </div>
        }
        return(
            <form onSubmit = {this.handleSubmit} className="form-container">
                <div className="form-row">
                    <h4>Track Requested Shipments </h4>
                    <input type="text" 
                    placeholder="e.g 101"
                    value={this.state.requestID}
                    ref= {this.requestIdRef}
                    onChange ={this.handleChange}
                    />
                    <input style={{cursor: 'pointer'}} type="submit" className="btn" value="VIEW STATUS"/>
                </div>

            </form>
        );
    }

}



export default Track
