import React, { Component } from "react";

class FinancialLog extends Component {
  state = { id: "" };
  constructor(props) {
    super(props);
    this.productIdRef = React.createRef();
    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  onSubmit = async (e) => {
    e.preventDefault();
    const proId = this.state.id;
    const standard = await this.props.contract.methods
      .getStdCostPlan(proId)
      .call();
      console.log(standard);
    const stdCostData = standard.map( (costItem , index) => {
      let matCost = parseInt(costItem,10);
      let pkgCost = parseInt(costItem.packagingMaterialCost,10);
      let laborCost = parseInt(costItem.directLaborCost,10);
      let indirectManuCost= parseInt(costItem.totalIndirectCost,10);
      let marketingCost = parseInt(costItem.marketingCost,10);
      let researchCost = parseInt(costItem.researchCost,10);
      let costTotal = parseInt(costItem.CostTOT,10);
      console.log(matCost,pkgCost,laborCost,indirectManuCost,marketingCost,researchCost
        , costTotal)

      return(
        <tr key={index}>
          <td> {matCost} </td>
        </tr>
      );
    });
    this.setState({ standard , stdCostData , proId });
  };

  onChange = async (e) => {
    this.setState({
      id: this.productIdRef.current.value,
    });
  };
  render() {
    let account = this.props.accounts;
    let contract = this.props.contract;
    if (!account || !contract) {
      return <div>Loading ..... </div>;
    }
    return (
      <div>
        <form onSubmit={this.onSubmit} className="form-container">
          <div className="form-row">
            <h4>Review Financial Affairs</h4>
            <label style={{ marginRight: "5px" }}> Product ID: </label>
            <input
              type="text"
              placeholder="e.g. pro101"
              value={this.state.id}
              onChange={this.onChange}
              ref={this.productIdRef}
            />

            <input
              style={{ cursor: "pointer" }}
              type="submit"
              className="btn"
              value="VIEW FINANCIAL STATUS"
            />
          </div>
          <div className="costsClashContainer">
            <div className="std-cost-container">
              {this.state.stdCostData}
              <table className="cost-data">
                <thead>
                  <tr>
                    <th><strong>Product ID: </strong> {this.state.proId} </th>
                  </tr>
                </thead>

                <tbody>
                </tbody> 
              </table>
              </div>

            <div className="actual-cost-container">
            </div>
          </div>
        </form>
      </div>
    );
  }
}

export default FinancialLog;
