import React, { Component} from 'react'

const inventory= ['vitamin-a' , 'vitamin-b-complex', 'plastic'];
const items = inventory.map(item => {
        return `${item} `
})
console.log(items);

class InventoryItem extends Component {

    render() {
        return(
            <div className="item-container">
                <div className='item-card'>
                <img style={{width:"80px"}} src={require("../vitamin.svg")} alt="material"/>
                <h5> {this.props.item} </h5>
                <h6> IN STOCK: {100+932} KG </h6>
                </div>
            </div>
        );
    }
}
class Inventory extends Component {

    state = {stockCount: 0 }
    constructor(props) {
        super(props)
        this.calculateStock = this.calculateStock.bind(this);
    }


    calculateStock() {
        let count = 0;
        for (let i = 0 ; i <= inventory.length; i++) {
            count++;
        }

        this.setState(state => ({
            stockCount: count
        }));

    }

    render() {
        return (
            <div className = "inventory-container">
                <button className="btn" onClick = {this.calculateStock}> REFRESH STOCK </button>
                <InventoryItem item ='vitamin-a' style= {{display: 'inline'}}/>
                <InventoryItem item = 'vitamin-b-complex' style= {{display: 'inline'}}/>
                {/* <div style={{marginTop: '20px'}}> Current Stock: {this.state.stockCount} </div> */}
            </div>
        );
    }
}

export default Inventory
