import React, { Component } from "react";

class TrackRecord extends Component {
  constructor(props) {
    super(props);
  }
  render() {
    console.log(this.props.status)
    return (
      <div className="track-record-container"> 
      <img src={require("../valid.svg")} width = '70px' alt= 'valid'/>
      <ul className='record-list'>
         <li> {this.props.time}</li>
         <li> {this.props.ship}</li>
         <li> {this.props.temp}</li>
         <li> {this.props.humid}</li>
      </ul>

      </div>
    );
  }
}

class Track extends Component {
  state = { logs: [] };

  componentDidMount = async () => {};

  state = { requestID: "" };
  constructor(props) {
    super(props);
    this.requestIdRef = React.createRef();
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit = async (e) => {
    e.preventDefault();
    let id = parseInt(this.state.requestID, 10);
    // await this.props.contract.methods.createLog(1, 'SHIPPED' ,'20C', '25%', 'NORMAL').send({from: this.props.account})
    let response = await this.props.contract.methods.getTrackLogs(id).call();
    let log = response.map((item,index) => {
      response.forEach(element => {
        const shipStatus = element.shipStatus;
        const matStatus = element.materialStatus;
        const temp = element.currentTemp;
        const humid = element.currentHumidity;
        const time  = element.logTime;
        const timeUTC = new Date(0);
        console.log(shipStatus,matStatus,temp,humid,time,timeUTC.setUTCSeconds(time))
        this.setState({shipStatus,matStatus,temp,humid,time})      
      });
      // return <TrackRecord key={index}
      // status = {item}
      // time = {this.timeUTC.setUTCDate(this.time)}
      // ship = {this.shipStatus}
      // material = {this.matStatus}
      // temp = {this.temp}
      // humid = {this.humid}
      //  />
    })
    this.setState({ response, log });
    this.setState({
      requestID: "",
    });
  };

  handleChange = async (e) => {
    this.setState({
      requestID: this.requestIdRef.current.value,
    });
  };

  render() {
    let acc = this.props.account;
    let cont = this.props.contract;

    if (!acc || !cont) {
      return <div> Loading..... </div>;
    }
    return (
      <form onSubmit={this.handleSubmit} className="form-container">
        <div className="form-row">
          <h4>Track Requested Shipments </h4>
          <label style={{marginRight: '5px'}}> Request ID: </label>
          <input
            type="text"
            placeholder="e.g 101"
            value={this.state.requestID}
            ref={this.requestIdRef}
            onChange={this.handleChange}
          />
          <input
            style={{ cursor: "pointer" }}
            type="submit"
            className="btn"
            value="VIEW SHIPMENT STATUS"
          />
        </div>

        <div className="response-text">{this.state.log}</div>
      </form>
    );
  }
}

export default Track;
