import React, { Component } from 'react';

// let myStyle = {
//     display:'none'
// }

let suppliers = ["0x8660D38DCE7CC16d5fD6dc4b5Dc079109890E4A5",
                   "0x65e439CC22968836e8Db9e30B6450ef9d7816C2E", 
                   "0x913D7882cEF7F8243c26cEb67947C78981729ed5" ]

let costs = ["10","15","13","12","51","12","14","25","20","9","8","20","40"]

let types = ["active", "packaging", "support"]

// let inventoryCount = 0;

class ProductForm extends Component {
    state = {materialName: '' , materialsVisibility:  false , proName: '' }


    constructor(props) {
        super(props);
        this.productRef = React.createRef();
        this.materialRef = React.createRef();
    }

    componentDidMount = async() => {

        // if (!this.props.contract || !this.props.accounts) {
        //     return <h2> PLEASE WAIT... </h2>;
        // }

    };

    onSubmit = async (e) => {
        e.preventDefault();
        if(this.state.input === undefined) {

        }
        // this.props.contract.methods.addProduct('1', this.state.input).send({from: this.props.accounts[0]});
        // this.logProduct();
        const value = this.state.input
        this.setState({ input: value, materialsVisibility: true });
        // console.log(this.myStyle)
        console.log(this.state.input)
      }

    onChange = (e) => this.setState({ 
        matName : this.materialRef.current.value,
        proName : this.productRef.current.value });

    // logProduct = async () => {
    //     const product = await this.props.contract.methods.getProductDetails('5').call();
    //     console.table(product);
    // }

    // fakeTransact = async () => {
    //     //await contract.methods.set(5).send({ from: accounts[0] });
    //     this.props.contract.methods.set(5).send({from: this.props.accounts[0]})
    //     this.setState({materialsVisibility: !this.setState.materialsVisibility });
    //     console.log(this.state.materialsVisibility)
    // }


    render() {
        const vis = this.state.materialsVisibility ? 'show' : 'hide';
        // const random = Math.floor(Math.random() * 100);
        return (

            <form onSubmit= {this.onSubmit} className='form-container' method='post'>
                {/* <div className= {`${vis}`}> INVENTORY LEVEL: {inventoryCount} </div> */}
                <div className="form-row">
                    <h4> Enter Product </h4> 
                    {/* <select id="products">
                        <option> pro101 </option>
                    </select> */}
                    <input type="text"
                     ref={this.productRef}
                    placeholder="e.g. pro101" 
                    value={this.state.proName} />

                    <h4>Choose Material</h4>
                    {/* <input onChange= { this.onChange } type="text" id="product-name" 
                    placeholder="Enter Material Name" autoComplete="off"/>  */}
                    <select id= "materials"
                     value={this.state.materialName} 
                     onChange = {this.onChange} 
                     ref ={this.materialRef} >
                        <option id='1' value='vitamin-a'>VITAMIN A </option>
                        <option id='2' value='vitmain-b-complex'>VITAMIN B COMPLEX</option>
                        <option id='3' value='vitamin-c-extract'>VITAMIN C EXTRACT</option>
                        <option id='4' value='vitamin-d'>VITAMIN D</option>
                        <option id='5' value='potassium'>POTASSIUM</option>
                        <option id='6' value='zinc'>ZINC</option>
                        <option id='7' value='plastic'>PLASTIC</option>
                        <option id='8' value='glass'>GLASS</option>
                        <option id='9' value='wood'>WOOD</option>
                    </select>
                </div>
                <input style= {{ marginLeft: '10px', cursor:'pointer'}} className="btn" type="submit" value="SEARCH"/>
                <div id= 'material-content' className={`material-card ${vis}`} >   
                    <ul>
                        <li><strong>SUPPLIER:</strong> {suppliers[0]} </li>
                        <li><strong>MATERIAL ID:</strong> uid{14+2} </li>
                        <li><strong>MATERIAL NAME:</strong> {this.state.input.toUpperCase()} </li> 
                        <li> <strong>MATERIAL TYPE: </strong> {types[0]}</li>
                        <li> <strong>UNIT COST: {costs[1]} </strong> </li>
                    </ul>
                    <input  type="number" placeholder="amount (KGs)"/> 
                    <button onClick = {this.fakeTransact} className='btn'> REQUEST </button>
                </div>
                <div id= 'material-content' className={`material-card ${vis}`} >   
                    <ul>
                        <li><strong>SUPPLIER:</strong> {suppliers[1]} </li>
                        <li><strong>MATERIAL ID:</strong> uid{21+12} </li>
                        <li><strong>MATERIAL NAME:</strong> {this.state.input.toUpperCase()} </li> 
                        <li> <strong>MATERIAL TYPE: </strong> {types[0]}</li>
                        <li> <strong>UNIT COST: {costs[3]} </strong> </li>
                    </ul> 
                    <input  type="number" placeholder="amount (KGs)"/> 
                    <button onClick = {this.fakeTransact} className='btn'> REQUEST </button>
                </div>

            </form>

         );



    }
}

export default ProductForm;
