import React, { Component } from "react";
import { BrowserRouter, Route, NavLink } from "react-router-dom";

class CreateAccount extends Component {
    render() {
        let acc = this.props.accounts;
            let cont = this.props.contract;
            if (!acc || !cont) {
              return <div> Loading..... </div>;
            }
        return(
              <form onSubmit={this.handleSubmit} className="newform-container">
                <label> Product Name: </label>
                <input
                  onChange={this.handleChange}
                  ref={this.nameRef}
                  type="text"
                  placeholder="e.g. MyProduct"
                  value={this.state.productName}
                />
                <label> Product ID: </label>
                <input
                  onChange={this.handleChange}
                  ref={this.idRef}
                  type="text"
                  placeholder="e.g. pro101"
                  value={this.state.productId}
                />

                <div>
                  <input className="btn" type="submit" value="CREATE ACCOUNT" />
                </div>
                <div
                  style={{ marginTop: "20px" }}
                  className="notify-data-container notify-text "
                >
                  {this.state.msg}
                </div>
              </form>
        );
    }
}

class ManageAccount extends Component {
    render() {
        return(
            <div> Manage Your Bank Account </div>
        );
    }
}
class BankAccounts extends Component {

    // constructor(props) {
    //     super(props);
    // }

    render() {
        let acc = this.props.accounts;
        let cont = this.props.contract;
        if (!acc || !cont) {
        return <div> Loading..... </div>;
        }

        return (
            <BrowserRouter>
        <div className="product-form-container">
          <div className="side-nav">
            <ul className="mini-nav-list">
              <li className="link-item">
                <NavLink to="/bank-account/new">
                  + CREATE ACCOUNT
                </NavLink>
              </li>
              <li className="link-item">

                <NavLink to="/bank-account/manage">
                  + MANAGE ACCOUNT
                </NavLink>
              </li>

            </ul>
          </div>
          <div className="main-content">
            <Route
              path="/bank-account/new"
              exact
              render={(props) => (
                <CreateAccount
                  {...props}
                  account={this.props.accounts}
                  contract={this.props.contract}
                />
              )}
            />
            <Route
              path="/bank-account/manage"
              exact
              render={(props) => (
                <ManageAccount
                  {...props}
                  account={this.props.accounts}
                  contract={this.props.contract}
                />
              )}
            />
          </div>
        </div>
      </BrowserRouter>
        );
    }
}

export default BankAccounts;
