import React, { Component } from 'react' 

let matNo = 0;

class MaterialForm extends Component {
    state = { visible: false}
    render() {
        return(
            <div>
                 <div className="form-row">
                                <label> Material {matNo} Name: </label>
                                <input type="text" placeholder="e.g MyMaterial"/> 
                             </div>    
                            <div style={{}} className="form-row"> 
                                <label> Material {matNo} Strength: </label>
                                <input type="number" placeholder="e.g 100mg"/> 
                                <label> Material {matNo} Form: </label>
                                <input type="number" placeholder="e.g powder"/> 
                            </div>
            </div>
        );
    }
}


class AddProduct extends Component {
    // constructor(props) {
    //     super(props);
    // }
    toggleMaterialForm() {
        matNo++;
        console.log(matNo)
    }

    render() {
        return(
            <div className="product-form"> 
                <form className='form-container'>
                    <div class="form-row">
                        <h4> Add Product </h4>
                            <div className="form-row">
                                <label> Product Name: </label>
                                <input type="text" placeholder="e.g MyProduct"/>
                            </div>
                            <button onClick= {this.toggleMaterialForm}  className="btn"> Add Material </button>
                            <MaterialForm  />                         
                    </div>    
                </form>
            </div>
        );
    }
}

export default AddProduct
