import React, { Component } from 'react' 
import { BrowserRouter, Route, NavLink } from 'react-router-dom';



class CreateProduct extends Component {
    state = {productName: '' , productId: '' , productForm: ''}

    constructor(props) {
        super(props);
        this.nameRef= React.createRef();
        this.idRef= React.createRef();
        this.formRef= React.createRef();
        // this.handleNameChange = this.handleNameChange.bind(this);
        // this.handleIdChange = this.handleIdChange.bind(this);
        // this.handleFormChange = this.handleFormChange.bind(this);
         this.handleChange = this.handleChange.bind(this);
         this.handleSubmit = this.handleSubmit.bind(this);
        // this.handleSubmit = this.handleChange.bind(this);
    }

    sendData() {

    }

    handleSubmit= async (e) => {
        e.preventDefault();
        let name = this.state.productName;
        let id   = this.state.productId;
        let form = this.state.productForm; 
        this.setState({
            productName: '',
            productId: '',
            productForm: ''
        });
        // await this.props.contract.methods.addProduct(id,name,form).send({from: this.props.accounts[0]});
        console.log(name,id,form);
    }

    handleChange = async (e) => {
        this.setState({
            productName: this.nameRef.current.value,
            productId: this.idRef.current.value,
            productForm: this.formRef.current.value,
        })
        console.log(this.props.account, this.props.contract)
    }


    render () {
        return (
            <form onSubmit={this.handleSubmit} className='newform-container'>
                 <label> Product Name: </label>
                 <input onChange={this.handleChange} ref={this.nameRef} type="text" placeholder="e.g. MyProduct"
                 value= {this.state.productName}/>
                 <label> Product ID: </label>
                <input onChange = {this.handleChange} ref={this.idRef} type="text" placeholder="e.g. pro101"
                value= {this.state.productId}/>
                <label> Product Form: </label>
                <select value={this.state.productForm} ref={this.formRef} onChange = {this.handleChange}>
                    <option id='1' value='tablets'>TABLETS</option>
                    <option id='2' value='capsules'>CAPSULES</option>
                </select>
                <div> 
                   <input className="btn" type="submit" value="CREATE PRODUCT"/>
                </div>

            </form>
        );
    }
}

class AddMaterial extends Component {
    state = { proID: '', matName: '' , matType: '' , matStr: 0 , matForm: ''}
    constructor(props) {
        super(props)
        this.proIdRef = React.createRef();
        this.matNameRef = React.createRef();
        this.matTypeRef = React.createRef();
        this.matStrRef = React.createRef();
        this.matFormRef = React.createRef();
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit= async(e) => {
        e.preventDefault();
        let proID = this.state.proID;
        let name  = this.state.matName;
        let type = this.state.matType;
        let strength = this.state.matStr;
        let form = this.state.matForm;
        this.setState({
            proID: '',
            matName: '',
            matType: '',
            matStr: '',
            matForm: '',
        })
        console.log(proID,name,type,strength,form);
    }

    handleChange = async (e) => {
        this.setState({
            proID: this.proIdRef.current.value,
            matName: this.matNameRef.current.value,
            matType: this.matTypeRef.current.value,
            matStr: this.matStrRef.current.value,
            matForm: this.matFormRef.current.value,
        })

    }
    render () {
        return (
            <form onSubmit= {this.handleSubmit} className="newform-container">
                <label>Product ID: </label>
                <input value={this.state.proID} onChange={this.handleChange} ref={this.proIdRef} type="text" placeholder="e.g. pro101" autoComplete="off"/>
                <label> Material Name: </label>
                <select name="mat-name" value={this.state.matName}onChange={this.handleChange} ref={this.matNameRef}>
                    <option id='1' value='vitamin-a'>VITAMIN A </option>
                    <option id='2' value='vitmain-b-complex'>VITAMIN B COMPLEX</option>
                    <option id='3' value='vitamin-c-extract'>VITAMIN C EXTRACT</option>
                    <option id='4' value='vitamin-d'>VITAMIN D</option>
                    <option id='5' value='potassium'>POTASSIUM</option>
                    <option id='6' value='zinc'>ZINC</option>
                    <option id='7' value='plastic'>PLASTIC</option>
                    <option id='8' value='glass'>GLASS</option>
                    <option id='9' value='wood'>WOOD</option>
                </select>

                <label> Material Type: </label>
                <select name="mat-type" value={this.state.matType} onChange={this.handleChange} ref={this.matTypeRef}>
                    <option id='1' value='active'>ACTIVE</option>
                    <option id='2' value='support'>SUPPORT/SECONDARY</option>
                    <option id='3' value='packaging'>PACKAGING</option>
                </select>
                {/* <input value={this.state.matType} onChange={this.handleChange} type="radio" ref="matTypeRef" name="material-type" value="active"/> ACTIVE
                <input value={this.state.matType} onChange={this.handleChange} type="radio" ref="matTypeRef" name="material-type" value="supporting"/> SUPPORT
                <input value={this.state.matType} onChange={this.handleChange} type="radio" ref="matTypeRef" name="material-type" value="packaging"/> PACKAGING */}
                <label> Material Strength: </label>
                <input value={this.state.matStr} onChange={this.handleChange} ref={this.matStrRef} type="number" placeholder="e.g. 150 mg"/>
                <label> Material Form: </label>
                <select name="mat-form" value={this.state.matType}onChange={this.handleChange} ref={this.matFormRef}>
                    <option id='1' value='powder'>POWDER</option>
                    <option id='2' value='liquid'>LIQUID</option>
                </select>

                {/* <div>

                </div>
                 <div>

                </div>
                <div className="form-item">

                </div>
                 <div>

                 </div>
                 <div >

                </div> */}
            <div>
               <input className="btn" type="submit" value="ADD MATERIAL"/>
            </div>
        </form>
        );
    }
}


class AddProduct extends Component {

    render() {
        let account = this.props.accounts;
        let contract =this.props.contract;
        return(
            <BrowserRouter>
            <div className="product-form-container">
               <div className="side-nav"> 
                    <ul className='mini-nav-list'>
                        <li className="link-item"><NavLink to="/add-product/createProduct">+ CREATE PRODUCT</NavLink> </li>
                        <li className="link-item"> <NavLink to="/add-product/addMaterial">+ ADD SPECS</NavLink></li>
                    </ul>
                </div>
                <div className="main-content">
                <Route path="/add-product/createProduct" exact component={CreateProduct}
                 render={props => (
                    <CreateProduct{...props} account={{account}} contract={{contract}} />
                  )}
                 />
                <Route path="/add-product/addMaterial" exact component={AddMaterial} />   
                </div>

            </div>


            </BrowserRouter>
        );
    }
}

export default AddProduct
