import React, { Component } from 'react' 
import { BrowserRouter, Route, NavLink } from 'react-router-dom';
//  import getWeb3 from "./getWeb3";


class CreateProduct extends Component {
    state = {productName: '' , productId: '' , productForm: ''}

    constructor(props) {
        super(props);
        this.nameRef= React.createRef();
        this.idRef= React.createRef();
        this.formRef= React.createRef();
        // this.handleNameChange = this.handleNameChange.bind(this);
        // this.handleIdChange = this.handleIdChange.bind(this);
        // this.handleFormChange = this.handleFormChange.bind(this);
         this.handleChange = this.handleChange.bind(this);
        // this.handleSubmit = this.handleChange.bind(this);
    }

    handleSubmit= async (e) => {
        let name = this.state.productName;
        let id   = this.state.productId;
        let form = this.state.productForm;
        e.preventDefault();
        this.setState({
            productName: '',
            productId: '',
            productForm: ''
        })
        console.log(name,id,form);
    }

    handleChange = async (e) => {
        this.setState({
            productName: this.nameRef.current.value,
            productId: this.idRef.current.value,
            productForm: this.formRef.current.value,
        })

    }


    render () {
        return (
            <form onSubmit={this.handleSubmit} className='newform-container'>
                 <label> Product Name: </label>
                 <input onChange={this.handleChange} ref={this.nameRef} type="text" placeholder="e.g. MyMaterial"
                 value= {this.state.productName}/>
                 <label> Product ID: </label>
                <input onChange = {this.handleChange} ref={this.idRef} type="text" placeholder="e.g. pro101"
                value= {this.state.productId}/>
                <label> Product Form: </label>
                <select value={this.state.productForm} ref={this.formRef} onChange = {this.handleChange}>
                    <option id='1' value='tablets'>TABLETS</option>
                    <option id='2' value='capsules'>CAPSULES</option>
                </select>
                <div> 
                   <input className="btn" type="submit" value="CREATE PRODUCT"/>
                </div>

            </form>
        );
    }
}

class AddMaterial extends Component {
    render () {
        return (
            <form className="newform-container">
                <label>Product ID: </label>
                <input type="text" placeholder="e.g. pro101" autoComplete="off"/>
                <label> Material Name: </label>
                <select>
                    <option id='1' value='vitamin-a'>VITAMIN A </option>
                    <option id='2' value='vitmain-b-complex'>VITAMIN B COMPLEX</option>
                    <option id='3' value='vitamin-c-extract'>VITAMIN C EXTRACT</option>
                    <option id='4' value='vitamin-d'>VITAMIN D</option>
                    <option id='5' value='potassium'>POTASSIUM</option>
                    <option id='6' value='zinc'>ZINC</option>
                    <option id='7' value='plastic'>PLASTIC</option>
                    <option id='8' value='glass'>GLASS</option>
                    <option id='9' value='wood'>WOOD</option>
                </select>
                <div>
                <label> Material Type: </label>
                <input type="radio" name="material-type" value="active"/> ACTIVE
                <input type="radio" name="material-type" value="supporting"/> SUPPORT
                <input type="radio" name="material-type" value="packaging"/> PACKAGING
                </div>
                <label> Material Strength: </label>
                <input type="number" placeholder="e.g. 150mg"/>
                <label> Material Form: </label>
                <input type="text" placeholder="e.g. powder" autoComplete="off"/>

                <div>

                </div>
                 <div>

                </div>
                <div className="form-item">

                </div>
                 <div>

                 </div>
                 <div >

                </div>
            <div>
               <input className="btn" type="submit" value="ADD MATERIAL"/>
            </div>
        </form>
        );
    }
}


class AddProduct extends Component {

    render() {
        console.log('from add product', this.props.accounts)
        return(
            <BrowserRouter>
            <div> {this.props.accounts} </div>
            <div className="product-form-container">
               <div className="side-nav"> 
                    <ul className='mini-nav-list'>
                        <li className="link-item"><NavLink to="/add-product/createProduct">+ CREATE PRODUCT</NavLink> </li>
                        <li className="link-item"> <NavLink to="/add-product/addMaterial">+ ADD SPECS</NavLink></li>
                    </ul>
                </div>
                <div className="main-content">
                <Route path="/add-product/createProduct" exact component={CreateProduct} />
                <Route path="/add-product/addMaterial" exact component={AddMaterial} />   
                </div>

            </div>


            </BrowserRouter>
        );
    }
}

export default AddProduct
