import React, { Component} from 'react'

const iventory= ['vitamin-a , vitamin-b-complex'];

class Inventory extends Component {

    state = {stockCount: 0 }
    constructor(props) {
        super(props)
        this.calculateStock = this.calculateStock.bind(this);
    }


    calculateStock() {
        let count;
        for (let i =0 ; i<this.inventory.length; i++) {
            count++;
        }

        this.setState(state => ({
            stockCount: count
        }));

    }

    render() {
        return (
            <div className = "inventory-container">
                <h3 style={{textAlign: 'left'}}> YOUR INVENTORY </h3>
                <button onClick = {this.calculateStock}> show inventory </button>
                <div> current stock: {this.state.stockCount} </div>
            </div>
        );
    }
}

export default Inventory
