import React, { Component } from "react";

class FinancialLog extends Component {
  state = { product: "" , actualCost: [] , stdCost: [] };
  constructor(props) {
    super(props);
    this.productIdRef = React.createRef();
    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  onSubmit = async (e) => {
    e.preventDefault();
    const proId = this.setState.product;
    let standard = await this.props.contract.methods.getStdCostPlan(proId);
    console.log(standard);
    let costs = standard.map((item) => {
      return item;
    })
    this.setState ({stdCost: this.state.stCost.push(costs)});

    this.setState({product: ''})
  };

  onChange = async (e) => {
    this.setState({
      product: this.productIdRef.current.value,
    });
  };
  render() {
    let account = this.props.accounts;
    let contract = this.props.contract;
    if (!account || !contract) {
      return <div>Loading ..... </div>;
    }
    return (
        <div>
        <form onSubmit={this.onSubmit} className="form-container">
        <div className="form-row">
          <h4>Review Financial Affairs</h4>
          <label style={{marginRight: '5px'}}> Product ID: </label>
          <input
            type="text"
            placeholder="e.g. pro101"
            value={this.state.product}
            ref={this.productIdRef}
            onChange={this.onChange}
          />
          <input
            style={{ cursor: "pointer" }}
            type="submit"
            className="btn"
            value="VIEW FINANCIAL STATUS"
          />
        </div>
        <div className="costsClashContainer">
          <div className="std-cost-container">
            {this.state.stdCost}
          </div>

          <div className="actual-cost-container">

          </div>
        </div>
        </form>


        </div>
    );
  }
}

export default FinancialLog;
