import React , { Component} from 'react'

class FinancialLog extends Component {
    state = {product: ''}
    constructor(props) {
        super(props);
        this.productIdRef = React.createRef();
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

    onSubmit = async(e) => {
        e.preventDefault();
    }

    onChange = async (e) => {
        this.setState({
            product: this.productIdRef.current.value
        })


    }
    render() {
        return(
            <div>
                <h1> FINANCIAL LOG </h1>
                <form onSubmit = {this.onSubmit} className = "newform-container" >
                <label> Enter Product ID</label>
                <input type="text"
                value={this.state.product}
                ref = {this.productIdRef}
                onChange= {this.onChange}
                placeholder = "e.g. pro101"
                />



                </form>
            </div>
        );
    }
}

export default FinancialLog
