class QueryProductSpecs extends Component {

    render() {
        return(
            <form>
                <h4> Enter Product </h4> 
                    {/* <select id="products">
                        <option> pro101 </option>
                    </select> */}
                    <input type="text"
                    ref={this.productRef}
                    placeholder="e.g. pro101" 
                    value={this.state.proName} 
                    onChange={this.onChange}/>

                      {/* <input 
                      style= {{cursor:'pointer'}}
                      className="btn-alt"
                      type="submit"
                      onClick = {this.viewSpecs}
                      value="VIEW PRODUCT SPECS"/> */}
                      <button
                      style= {{cursor:'pointer'}}
                      className="btn-alt"
                      type="submit"
                      onClick = {this.viewSpecs}

                      >
                          VIEW PRODUCT SPECS
                      </button>
                    <div style={{marginTop: '20px', fontStyle: 'italic'}} className='product-data-container'>
                        {this.state.specs}
                    </div>
            </form>
        );
    }
}

class RequestMaterials extends Component {
    render() {
        return(
            <div> Request Material </div>
        )
    }
}

class ProductForm extends Component {
    state = {materialName: '' , materialsVisibility:  false , proName: '' , supplier: '' }


    constructor(props) {
        super(props);
        this.productRef = React.createRef();
        this.materialRef = React.createRef();
        this.supplierRef = React.createRef();
        this.onSubmit = this.onSubmit.bind(this)
