            // console.log()
            if(this.props.Web3.utils.isAddress(this.state.supplier)) {
                const materials = await this.props.contract.methods.getMaterialBySupplier(`${this.state.supplier}`).call()
                if (materials.length === 0) {
                    this.setState({materials: materials.push("This supplier has no materials created currently!")})
                }
                this.setState({materials})
            }


        }
