                        <input style= {{ cursor:'pointer'}} className="btn" type="submit" value="SEARCH AVAILABLE MATERIAL"/> 

                        <div style={{marginTop: '20px', fontStyle: 'italic'}} className='material-data-container'>
                        {this.state.materials}
                    </div>
