import React, { Component } from "react";

class FinancialLog extends Component {
  state = { id: "" , tableVisibility: false };
  constructor(props) {
    super(props);
    this.productIdRef = React.createRef();
    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  onSubmit = async (e) => {
    e.preventDefault();
    const proId = this.state.id;
    const standard = await this.props.contract.methods
    .getStdCostPlan(proId)
    .call();
    const stdCostData = standard.map( (costList , index) => {
      let matCost = standard.directMaterialCost;
      let pkgCost = standard.packagingMaterialCost;
      let laborCost = standard.directLaborCost;
      let indirectManuCost = standard.totalIndirectCost;
      let mrkCost = standard.marketingCost;
      let rsrchCost= standard.researchCost;
      let totalCost = standard.CostTOT;
      // let stdCostList = costList.standard.packagingMaterialCost;
      console.log(matCost,pkgCost,laborCost,indirectManuCost,mrkCost,rsrchCost,totalCost)
      this.setState({ standard , proId, matCost, pkgCost, laborCost,
        indirectManuCost, mrkCost , rsrchCost, totalCost });
        return(
          true
        );

    });
    this.setState({stdCostData , tableVisibility:true});

  };

  onChange = async (e) => {
    this.setState({
      id: this.productIdRef.current.value,
    });
  };
  render() {
    let table;
    let account = this.props.accounts;
    let contract = this.props.contract;
    if (!account || !contract) {
      return <div>Loading ..... </div>;
    }
    this.state.tableVisibility ? (table = "show") : (table = "hide");
    return (
      <div>
        <form onSubmit={this.onSubmit} className="form-container">
          <div className="form-row">
            <h4>Review Financial Affairs</h4>
            <label style={{ marginRight: "5px" }}> Product ID: </label>
            <input
              type="text"
              placeholder="e.g. pro101"
              value={this.state.id}
              onChange={this.onChange}
              ref={this.productIdRef}
            />

            <input
              style={{ cursor: "pointer" }}
              type="submit"
              className="btn"
              value="VIEW FINANCIAL STATUS"
            />
          </div>
          <div className="product-info"> 
          <strong>Product ID: </strong> {this.state.proId} 
          </div>
          <div className ={`${table} costsClashContainer `} >
            <div className="std-cost-container">
              <table  className="cost-data">
                <thead>
                  <tr>
                    <th>CRITERIA</th>
                    <th>VALUE</th>
                  </tr>
                </thead>
                <tbody>

                <tr>
                  <td> Raw Materials </td>
                  <td>{this.state.matCost}</td>
                </tr>
                <tr>
                  <td> Packaging Materials </td>
                  <td>{this.state.pkgCost}</td>
                </tr>
                <tr>
                  <td> Direct Labor </td>
                  <td>{this.state.laborCost}</td>
                </tr>
                <tr>
                  <td> Indirect Manufacturing Costs </td>
                  <td>{this.state.indirectManuCost}</td>
                </tr>
                <tr>
                  <td> Marketing </td>
                  <td>{this.state.mrkCost}</td>
                </tr>
                <tr>
                  <td> Research </td>
                  <td>{this.state.rsrchCost}</td>
                </tr>
                </tbody>

                <tfoot>
                  <tr>
                    <th> TOTAL </th>
                    <td>{this.state.totalCost} </td> 
                  </tr>
                </tfoot> 
              </table>
              </div>

            <div className="actual-cost-container">
            <table  className="cost-data">
                <thead>
                  <tr>
                    <th>CRITERIA</th>
                    <th>VALUE</th>
                  </tr>
                </thead>
                <tbody>

                <tr>
                  <td> Raw Materials </td>
                  <td>{this.state.matCost}</td>
                </tr>
                <tr>
                  <td> Packaging Materials </td>
                  <td>{this.state.pkgCost}</td>
                </tr>
                <tr>
                  <td> Direct Labor </td>
                  <td>{this.state.laborCost}</td>
                </tr>
                <tr>
                  <td> Indirect Manufacturing Costs </td>
                  <td>{this.state.indirectManuCost}</td>
                </tr>
                <tr>
                  <td> Marketing </td>
                  <td>{this.state.mrkCost}</td>
                </tr>
                <tr>
                  <td> Research </td>
                  <td>{this.state.rsrchCost}</td>
                </tr>
                </tbody>

                <tfoot>
                  <tr>
                    <th> TOTAL </th>
                    <td>{this.state.totalCost} </td> 
                  </tr>
                </tfoot> 
              </table>
            </div>
          </div>
        </form>
      </div>
    );
  }
}

export default FinancialLog;
