class AddProduct extends Component {

    constructor(props) {
        super(props);
        this.toggleMaterialForm = this.toggleMaterialForm.bind(this)
    }


    toggleMaterialForm() {
    }

    render() {
        return(
            <div className="product-form"> 
                <form className='form-container'>
                    <div class="form-row">
                        <h4> Add Product Details </h4>
                            <div className="form-row">
                                <label> Product Name: </label>
                                <input type="text" placeholder="e.g MyProduct" autoComplete="off"/>
                            </div>
                            <div className="form-row">
                                <label> Product ID: </label>
                                <input type="text" placeholder="e.g pro101" autoComplete="off"/>
                            </div>
                            <div className="form-row">
                                <label> Add Material: </label>
                                <select onChange = {this.onChange}>
                                    <option id='1' value='vitamin-a'>VITAMIN A </option>
                                    <option id='2' value='vitmain-b-complex'>VITAMIN B COMPLEX</option>
                                    <option id='3' value='vitamin-c-extract'>VITAMIN C EXTRACT</option>
                                    <option id='4' value='vitamin-d'>VITAMIN D</option>
                                    <option id='5' value='potassium'>POTASSIUM</option>
                                    <option id='6' value='zinc'>ZINC</option>
                                    <option id='7' value='plastic'>PLASTIC</option>
                                    <option id='8' value='glass'>GLASS</option>
                                    <option id='9' value='wood'>WOOD</option>
                              </select>
                            </div>
                            <div className="form-row">
                                <label>Material Strength: </label>
                                <input type="number" placeholder="e.g. 150 mg"/>
                            </div>
                            <div className="form-row">
                                <label>Material Form: </label>
                                <input type="text" placeholder="e.g. powder"/>
                            </div>

                            <button onClick= {this.toggleMaterialForm}  className="btn"> CREATE PRODUCT </button>                       
                    </div>    
                </form>
            </div>
        );
    }
}

export default AddProduct
